import React from "react";
import { connect } from "react-redux";
import { firestoreConnect } from "react-redux-firebase";
import { deleteTask, editTaskButton } from "../store/actions";
import { compose } from "redux";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import moment from "moment";

const Task = ({ task, deleteTask, editTaskButton }) => {
  const { id, category, name, stringDate, priority } = task;

  return (
    <div className="singleTask">
      <div className="task">
        {/* NAME AND PRIORITY */}
        <div className="firstRow">
          <div className="name">
            <h2>{name}</h2>
          </div>
          <div className="priority">
            <h3>Priority: {priority}</h3>
          </div>
        </div>
        {/* DATE AND CATEGORY */}
        <div className="secondRow">
          <div className="date">Do it: </div>
          <div className="category">
            Category: <span>{category}</span>
          </div>
          <div>Task created:</div>
        </div>
      </div>
      {/* EDIT AND DELETE */}
      <div className="editAndDelete">
        <div className="button" onClick={() => editTaskButton(task)}>

        </div>
        <div className="button" onClick={() => deleteTask(task)}>
          <FontAwesomeIcon icon={faCoffee} />
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state, ownProps) => {
  // console.log(state.firestore.data.Tasks);
  // const id = ownProps.match.params.id;
  // console.log(ownProps);
  // console.log(state);

  return {};
};

export default compose(
  connect(mapStateToProps, { deleteTask, editTaskButton }),
  firestoreConnect(() => [{ collection: "Tasks" }])
)(Task);

